declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Account (\n  \"accountType\" TEXT NOT NULL DEFAULT 'UNK' CHECK(`accountType` in ('VAN', 'CAT', 'TS', 'PDI', 'NB', 'FB', 'TW', 'LI', 'UNK')),\n  \"accountId\" TEXT NOT NULL,\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT AccountPk PRIMARY KEY (\"personId\", \"accountType\", \"accountId\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS AccountAccountIdIndex ON \"Account\" (\"accountId\");\n\n";
export default SQL_QUERY;
