declare const SQL_QUERY = "\nUPDATE OR IGNORE Account SET\n  \"accountType\" = IFNULL(@accountType, \"accountType\"), \"accountId\" = IFNULL(@accountId, \"accountId\"), \"createdAt\" = IFNULL(@createdAt, \"createdAt\"), \"updatedAt\" = IFNULL(@updatedAt, \"updatedAt\"), \"deletedAt\" = IFNULL(@deletedAt, \"deletedAt\"), \"personId\" = IFNULL(@personId, \"personId\")\nWHERE\n  \"personId\" = @personId AND \"accountType\" = @accountType AND \"accountId\" = @accountId\n";
export default SQL_QUERY;
