declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Campaign (\n  \"id\" TEXT NOT NULL,\n  \"name\" TEXT NOT NULL,\n  \"contestId\" TEXT NOT NULL REFERENCES Contest(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT CampaignPk PRIMARY KEY (\"id\")\n) WITHOUT ROWID;\n\n";
export default SQL_QUERY;
