declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Email (\n  \"email\" TEXT NOT NULL,\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"deliverable\" INTEGER,\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT EmailPk PRIMARY KEY (\"personId\", \"email\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS EmailEmailIndex ON \"Email\" (\"email\");\n\n";
export default SQL_QUERY;
