declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Experience (\n  \"type\" TEXT NOT NULL DEFAULT 'UNK' CHECK(`type` in ('FULL', 'PART', 'CONT', 'VOL', 'SELF', 'FREE', 'APPR', 'SEAS', 'UNK')),\n  \"employer\" TEXT NOT NULL,\n  \"role\" TEXT NOT NULL,\n  \"startDate\" INTEGER,\n  \"endDate\" INTEGER,\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT ExperiencePk PRIMARY KEY (\"personId\", \"type\", \"employer\", \"role\")\n) WITHOUT ROWID;\n\n";
export default SQL_QUERY;
