declare const SQL_QUERY = "\nINSERT INTO Experience (\n  \"type\", \"employer\", \"role\", \"startDate\", \"endDate\", \"createdAt\", \"updatedAt\", \"deletedAt\", \"personId\"\n) VALUES (\n  @type, @employer, @role, @startDate, @endDate, @createdAt, @updatedAt, @deletedAt, @personId\n)\nON CONFLICT (\"personId\", \"type\", \"employer\", \"role\") DO UPDATE SET\n\"startDate\" = IFNULL(@startDate, \"startDate\"), \"endDate\" = IFNULL(@endDate, \"endDate\"), \"createdAt\" = MIN(@createdAt, \"createdAt\"), \"updatedAt\" = MAX(@updatedAt, \"updatedAt\"), \"deletedAt\" = IFNULL(@deletedAt, \"deletedAt\")\n";
export default SQL_QUERY;
