declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS PersonAddress (\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"ncoa\" TEXT,\n  \"type\" TEXT CHECK(`type` in ('RENT', 'CONT', 'SUB', 'BMR', 'OWN', 'LOWN', 'RES', 'MAIL', 'WORK', 'INV', 'OTHER', NULL)),\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  \"addressId\" TEXT NOT NULL REFERENCES Address(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT PersonAddressPk PRIMARY KEY (\"personId\", \"addressId\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS PersonAddressAddressIndex ON \"PersonAddress\" (\"addressId\");\n\n";
export default SQL_QUERY;
