declare const SQL_QUERY = "\nUPDATE OR IGNORE PersonAddress SET\n  \"createdAt\" = IFNULL(@createdAt, \"createdAt\"), \"updatedAt\" = IFNULL(@updatedAt, \"updatedAt\"), \"deletedAt\" = IFNULL(@deletedAt, \"deletedAt\"), \"ncoa\" = IFNULL(@ncoa, \"ncoa\"), \"type\" = IFNULL(@type, \"type\"), \"personId\" = IFNULL(@personId, \"personId\"), \"addressId\" = IFNULL(@addressId, \"addressId\")\nWHERE\n  \"personId\" = @personId AND \"addressId\" = @addressId\n";
export default SQL_QUERY;
