declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS PersonPhone (\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"type\" TEXT CHECK(`type` in ('HOME', 'PERS', 'WORK', 'OTHER', NULL)),\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  \"phoneId\" TEXT NOT NULL REFERENCES Phone(number) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT PersonPhonePk PRIMARY KEY (\"personId\", \"phoneId\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS PersonPhonePhoneIndex ON \"PersonPhone\" (\"phoneId\");\n\n";
export default SQL_QUERY;
