declare const SQL_QUERY = "\nUPDATE OR IGNORE Score SET\n  \"name\" = IFNULL(\"name\", @name), \"value\" = IFNULL(\"value\", @value), \"updatedAt\" = IFNULL(\"updatedAt\", @updatedAt), \"personId\" = IFNULL(\"personId\", @personId)\nWHERE\n  \"personId\" = @personId AND \"name\" = @name\n";
export default SQL_QUERY;
