declare const SQL_QUERY = "\nINSERT INTO Score (\n  \"name\", \"value\", \"updatedAt\", \"personId\"\n) VALUES (\n  @name, @value, @updatedAt, @personId\n)\nON CONFLICT (\"personId\", \"name\") DO UPDATE SET\n\"value\" = IFNULL(@value, \"value\"), \"updatedAt\" = IFNULL(@updatedAt, \"updatedAt\")\n";
export default SQL_QUERY;
