declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Turf (\n  \"id\" TEXT NOT NULL,\n  \"shapeId\" INTEGER NOT NULL,\n  \"createdAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"updatedAt\" INTEGER NOT NULL DEFAULT CURRENT_TIMESTAMP,\n  \"deletedAt\" INTEGER,\n  \"type\" TEXT NOT NULL DEFAULT 'CUSTOM' CHECK(`type` in ('ADMIN', 'LEGIS', 'LOCAL', 'POSTAL', 'STAT', 'CUSTOM')),\n  \"state\" INTEGER,\n  \"county\" INTEGER,\n  \"group\" TEXT NOT NULL,\n  \"name\" TEXT NOT NULL,\n  \"lat\" REAL,\n  \"lng\" REAL,\n  \"latMin\" REAL,\n  \"latMax\" REAL,\n  \"lngMin\" REAL,\n  \"lngMax\" REAL,\n  \"area\" REAL,\n  CONSTRAINT TurfPk PRIMARY KEY (\"id\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS TurfNameIndex ON \"Turf\" (\"name\");\nCREATE INDEX IF NOT EXISTS TurfBoundsIndex ON \"Turf\" (\"latMin\", \"latMax\", \"lngMin\", \"lngMax\");\nCREATE VIRTUAL TABLE IF NOT EXISTS TurfShape USING geopoly();\n";
export default SQL_QUERY;
