declare const SQL_QUERY = "\nCREATE TABLE IF NOT EXISTS Vote (\n  \"method\" TEXT CHECK(`method` in ('PL', 'AB', 'DB', 'PO', 'PD', 'MO', 'MD', 'NR', NULL)),\n  \"party\" TEXT CHECK(`party` in ('D', 'R', 'G', 'L', 'OL', 'OC', 'OU', 'NPP', 'U', NULL)),\n  \"issued\" INTEGER,\n  \"returned\" INTEGER,\n  \"counted\" INTEGER,\n  \"personId\" TEXT NOT NULL REFERENCES Person(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  \"electionId\" TEXT NOT NULL REFERENCES Election(id) ON UPDATE CASCADE ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,\n  CONSTRAINT VotePk PRIMARY KEY (\"personId\", \"electionId\")\n) WITHOUT ROWID;\nCREATE INDEX IF NOT EXISTS VotePersonIndex ON \"Vote\" (\"personId\");\n\n";
export default SQL_QUERY;
